create table COUNTRIES
(
    COUNTRY_ID    VARCHAR(2)  NOT NULL,
    COUNTRY_NAME  VARCHAR(40) NOT NULL,
    REGION_ID     INT,
    PRIMARY KEY (COUNTRY_ID)
);



create table LOCATIONS (
    LOCATION_ID    INT NOT NULL AUTO_INCREMENT,
    STREET_ADDRESS VARCHAR(40) NOT NULL,
    POSTAL_CODE    VARCHAR(12),
    CITY           VARCHAR(30) not null,
    STATE_PROVINCE VARCHAR(25),
    COUNTRY_ID     VARCHAR(2) NOT NULL,
    PRIMARY KEY (LOCATION_ID),
    CONSTRAINT FK_LOCATIONS_COUNTRY_ID FOREIGN KEY (COUNTRY_ID)
        REFERENCES COUNTRIES (COUNTRY_ID)
);


create table DEPARTMENTS
(
    DEPARTMENT_ID   INT NOT NULL AUTO_INCREMENT,
    DEPARTMENT_NAME VARCHAR(30) NOT NULL UNIQUE,
    LOCATION_ID     INT         NOT NULL,
    PRIMARY KEY (DEPARTMENT_ID),
    CONSTRAINT FK_DEPARTMENTS_LOCATION_ID FOREIGN KEY (LOCATION_ID)
         REFERENCES LOCATIONS (LOCATION_ID)
);


create table EMPLOYEES
(
    EMPLOYEE_ID    INT NOT NULL AUTO_INCREMENT,
    FIRST_NAME     VARCHAR(20) NOT NULL,
    LAST_NAME      VARCHAR(25) NOT NULL,
    EMAIL          VARCHAR(25) NOT NULL UNIQUE,
    PHONE_NUMBER   VARCHAR(20),
    HIRE_DATE      DATE         NOT NULL,
    JOB_ID         VARCHAR(10) NOT NULL,
    SALARY         DECIMAL(8, 2),
    COMMISSION_PCT DECIMAL(2, 2),
    MANAGER_ID     INT,
    DEPARTMENT_ID  INT,
    primary key (EMPLOYEE_ID),
    constraint fk_employees_manager_id foreign key (MANAGER_ID)
        references EMPLOYEES (EMPLOYEE_ID),
    CONSTRAINT FK_EMPLOYEES_DEPARTMENT_ID foreign KEY (DEPARTMENT_ID)
        REFERENCES DEPARTMENTS (DEPARTMENT_ID),
    CONSTRAINT CK_EMPLOYEES_SALARY CHECK (SALARY > 0)
);



commit;



